mapper.select:
SELECT * FROM bl_device WHERE 1=1
AND device_id like concat('%',#{deviceId},'%')
AND pc_id like concat('%',#{pcId},'%')
AND name like concat('%',#{name},'%')
AND status =#{status}
order by updated_at DESC LIMIT ${startNum},${pageSize}

SELECT * FROM bl_device_advert d 
WHERE d.`device_id`=#{deviceId}  
ORDER BY d.`weight`

SELECT * FROM bl_device_game d 
WHERE d.`device_id`=#{deviceId}  
ORDER BY d.`weight`
