mapper.select:
SELECT * FROM bl_version
WHERE 1=1

	AND screen =#{screen}


	AND status =#{status}

ORDER BY created_at DESC
LIMIT ${startNum},${pageSize}

SELECT COUNT(1)	FROM bl_version
WHERE 1=1

	AND screen =#{screen}


	AND status =#{status}

SELECT * 
FROM bl_version_downloader 
WHERE 1=1

	AND status =#{status}

ORDER BY created_at DESC
LIMIT ${startNum},${pageSize}
